function getRainMinutes(dataPoints, msg, skip) {
    // node.warn(dataPoints);
    for(i=skip;i<dataPoints.length;i++){
    var dataPoint = dataPoints[i];
    if(dataPoint.precipProbability>lowerProbability){
        msg.minutes = (dataPoint.time*1000-Date.now())/(60*1000);
        msg.timestamp = dataPoint.time*1000;
        msg.payload = dataPoint.time;
//        node.warn(msg.minutes);
//        node.warn(i);
        return msg;
    }
    msg.minutes = -1;
    return msg;
    }
}

