    msg = getRainMinutes(msg.data.hourly.data, msg,2);
}

// formatting
msg.minutes = Math.round(msg.minutes);
var date = new Date(msg.timestamp);
var day = date.getDate();
var today =  new Date().getDate(); // Date.now().getDate();
var hour = date.getHours();
if(day == today){
    msg.date = "Today around "+hour +":00"
} else{
    msg.date = "Tomorrow around "+hour +":00"
}
return msg;

if(msg.payload === false) return [null,msg,null];
