if(msg.payload === true) return [msg,null,null];
return [null,null,msg];

var v = msg.payload;
msg.payload = v;
msg.from = v % 10;
msg.to = Math.floor(v / 1000);
switch (v){
    case 7000:
        msg.type = "wake";
        break;
    case 7007:
        msg.type = "shake";
        break;
    case 7008:
        msg.type = "drop";        
        break;
    default:
        switch(msg.from){
            case 0:
                msg.type = "push";
                break;
            default:
                if(msg.from == msg. to){
                    msg.type = "double tap";
                }else{
                    msg.type = "rotate";
                }
        }
}
return msg;

